function factorial(n) {
  if (n === 0) {
    return 1;
  } else {
    return n * factorial(n - 1);
  }
}

console.log(factorial(5)); // Виведе: 120

function traverseTree(node) {
  console.log(node.value);

  if (node.left) {
    traverseTree(node.left);
  }

  if (node.right) {
    traverseTree(node.right);
  }
}

